// create local state to gather form data
// dispatch to redux-thunk for async authentication
// on validation redirect to dashboard page

import React, { useState } from "react";
import { useDispatch } from "react-redux";


import styles from './LoginModal.module.css';

const LoginModal = () => {
  // set up local state to collect form data
  const initialState = {
    user: '',
    password: '',
  };

  const [formData, setFormData] = useState(initialState);

  const userHandler = e => {
    setFormData(currentState => {
      return {
        ...currentState,
        user: e.target.value,
      }
    })
  }

  const passwordHandler = e => {
    setFormData(currentState => {
      return {
        ...currentState,
        password: e.target.value,
      }
    })
  }

  // dispatch to redux-thunk for authentication
  const dispatch = useDispatch();


  const loginAttemptHandler = () => {
    console.log('dispatch state for authentication')
  }

  return (
    <React.Fragment>
      <div className={styles.modalContainer}>
        <h2></h2>

        <label htmlFor="nickname" >Project Nickname* </label>
        <input type="text" 
                name="nickname" 
                id="nickname" 
                value={formData.nickname}
                required={true} 
                onChange={nicknameHandler} >
        </input>
      </div>
    </React.Fragment>
  )
};

export default LoginModal;
