import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { 
  changeNickname, 
  changeLocation, 
  changeClient, 
  selectNickname,
} from './projectFormSlice';

import styles from './ProjectForm.module.css';

const ProjectForm = () => {
  // Handle Local State for Form Data
  const [formData, setFormData] = useState({
    // -- orderState property may be edited in future feature
    orderState: 'Draft',
    nickname: '',
    location: '',
    client: '',
    // company: '',
    // deliveryDate: '',
    // rentalTerm: '',
    // tag: '',
  });

  const nicknameHandler = (e) => {
    setFormData(currentState => {
      return {
        ...currentState,
        nickname: e.target.value,
      }
    })
  };

  const locationHandler = (e) => {
    setFormData(currentState => {
      return {
        ...currentState,
        location: e.target.value,
      }
    })
  };

  const clientHandler = (e) => {
    setFormData(currentState => {
      return {
        ...currentState,
        client: e.target.value,
      }
    })
  };


  // ---- Handle Form Submission To Update Store ... and Post to DB
  const dispatch = useDispatch();

  const formSubmissionHandler = e => {
    e.preventDefault();
    // dispatch form data to store
    console.log('form submitted')
  }

  console.log(formData)

  return (
    <form className={styles.form} onSubmit={formSubmissionHandler}>
      <h2>Project Profile</h2>

      <label htmlFor="nickname" >Project Nickname* </label>
      <input type="text" 
              name="nickname" 
              id="nickname" 
              value={formData.nickname}
              required={true} 
              onChange={nicknameHandler} >
      </input>

      {/* refactor to pre-populate based on zip - JS30 */}
      <label htmlFor="location" >Location / City* </label>
      <input type="text" 
              name="location" 
              id="location"               value={formData.nickname}
              required={true} 
              onChange={nicknameHandler} >
      </input>

      <label htmlFor="client" >Client</label>
      <input type="text" name="client" id="client" ></input>

      <button type="submit" >Create Draft Project</button>
    </form>
  )
};

export default ProjectForm;










