import styles from './LoginModal.module.css';

const LoginModal = () => {
  // set up local state to collect form data
  const initialState = {
    user: '',
    password: '',
  };

  const [formData, setFormData] = useState(initialState);

  const userHandler = e => {
    setFormData(currentState => {
      return {
